// src/components/VideoCarousel.js
import React, { useState, useEffect } from 'react';
import '../../styles/VideoCarousel.css';

const videoData = [
  {
    src: '../../assets/intro-video',
    text: 'Welcome to ActAware! Discover our innovative sign language platform.'
  },
  {
    src: '../../assets/feature-video.mp4',
    text: 'Learn how our lessons work with interactive features and tools.'
  },
  {
    src: '../../assets/Thankyou.mp4',
    text: 'ThankYou for visting our website.'
  }
  
];

const VideoCarousel = () => {
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentVideoIndex(prevIndex => (prevIndex + 1) % videoData.length);
    }, 10000); // Change every 10 seconds
    return () => clearInterval(interval);
  }, []);

  const currentVideo = videoData[currentVideoIndex];

  return (
    <div className="video-carousel">
      <video 
        className="carousel-video" 
        src={currentVideo.src} 
        autoPlay 
        muted 
        loop 
        playsInline
      />
      <div className="video-overlay">
        <p>{currentVideo.text}</p>
      </div>
    </div>
  );
};

export default VideoCarousel;
