// src/components/Auth/ProfilePage.js
import React, { useState, useEffect } from "react";
import "../../styles/ProfilePage.css";

const ProfilePage = () => {
    const [formData, setFormData] = useState({
        fullName: "",
        email: "",
        phone: "",
        age: "",
        gender: "",
        location: "",
        education: "",
        occupation: "",
        goals: "",
        priorKnowledge: "",
        reasonForJoining: "",
        languagePreference: "",
        learningMode: "online",
        hasLaptopOrSmartphone: false,
        hasInternetAccess: false,
        referralSource: "",
        preferredInstructorGender: "noPreference",
    });

    // Load saved profile on mount
    useEffect(() => {
        const saved = localStorage.getItem("userProfile");
        if (saved) {
            setFormData(JSON.parse(saved));
        }
    }, []);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]:
                type === "checkbox"
                    ? checked
                    : value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        localStorage.setItem("userProfile", JSON.stringify(formData));
        alert("Profile saved!");
    };

    return (
        <div className="profile-page">
            <h1>Your Profile</h1>
            <form onSubmit={handleSubmit} className="profile-form">
                <div className="row">
                    <label>Full Name
                        <input
                            name="fullName"
                            value={formData.fullName}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>Email
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </div>

                <div className="row">
                    <label>Phone
                        <input
                            name="phone"
                            value={formData.phone}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Age
                        <input
                            type="number"
                            name="age"
                            value={formData.age}
                            onChange={handleChange}
                            min={0}
                        />
                    </label>
                </div>

                <div className="row">
                    <label>Gender
                        <select name="gender" value={formData.gender} onChange={handleChange}>
                            <option value="">Selectâ€¦</option>
                            <option value="male">Male</option>
                            <option value="female">Female</option>
                            <option value="preferNotToSay">Prefer not to say</option>
                        </select>
                    </label>

                    <label>Location
                        <input
                            name="location"
                            value={formData.location}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <div className="row">
                    <label>Education
                        <input
                            name="education"
                            value={formData.education}
                            onChange={handleChange}
                        />
                    </label>
                    <label>Occupation
                        <input
                            name="occupation"
                            value={formData.occupation}
                            onChange={handleChange}
                        />
                    </label>
                </div>

                <label>Goals
                    <textarea
                        name="goals"
                        value={formData.goals}
                        onChange={handleChange}
                    />
                </label>

                <label>Prior Knowledge
                    <textarea
                        name="priorKnowledge"
                        value={formData.priorKnowledge}
                        onChange={handleChange}
                    />
                </label>

                <label>Reason for Joining
                    <textarea
                        name="reasonForJoining"
                        value={formData.reasonForJoining}
                        onChange={handleChange}
                    />
                </label>

                <div className="row">
                    <label>Language Preference
                        <input
                            name="languagePreference"
                            value={formData.languagePreference}
                            onChange={handleChange}
                        />
                    </label>

                    <label>Learning Mode
                        <select
                            name="learningMode"
                            value={formData.learningMode}
                            onChange={handleChange}
                        >
                            <option value="online">Online</option>
                            <option value="offline">Offline</option>
                            <option value="hybrid">Hybrid</option>
                        </select>
                    </label>
                </div>

                <div className="row">
                    <label>
                        <input
                            type="checkbox"
                            name="hasLaptopOrSmartphone"
                            checked={formData.hasLaptopOrSmartphone}
                            onChange={handleChange}
                        /> Have Laptop/Smartphone
                    </label>

                    <label>
                        <input
                            type="checkbox"
                            name="hasInternetAccess"
                            checked={formData.hasInternetAccess}
                            onChange={handleChange}
                        /> Have Internet Access
                    </label>
                </div>

                <label>Referral Source
                    <input
                        name="referralSource"
                        value={formData.referralSource}
                        onChange={handleChange}
                    />
                </label>

                <label>Preferred Instructor Gender
                    <select
                        name="preferredInstructorGender"
                        value={formData.preferredInstructorGender}
                        onChange={handleChange}
                    >
                        <option value="noPreference">No Preference</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                    </select>
                </label>

                <button type="submit" className="save-btn">Save Profile</button>
            </form>
        </div>
    );
};

export default ProfilePag;
