import React from "react";
import "../../styles/AllCareersPage.css";

const careers = [
  {
    title: "Sign Language Interpreter",
    description:
      "Master essential sign language skills and interpretation techniques to bridge communication gaps between Deaf and hearing individuals in diverse settings.",
    like:
      "bridging communication gaps, interpreting live conversations, working in education and healthcare",
    salary: "₹30,000/month median salary",
    jobs: "12,000+ job opportunities",
    credentials: ["ASL Certification", "BS in Deaf Studies"],
    image: "sign-interpreter.png",
    color: "#D41B46", // A bold red
  },
  {
    title: "Deaf Educator",
    description:
      "Empower Deaf students with tailored educational programs that integrate sign language, cultural understanding, and modern teaching methodologies.",
    like:
      "developing inclusive curricula, mentoring Deaf students, fostering accessibility in classrooms",
    salary: "₹35,000/month median salary",
    jobs: "8,000+ job opportunities",
    credentials: ["MA in Deaf Education", "TESOL for ASL"],
    image: "deaf-educator.png",
    color: "#F39C12", // A warm amber
  },
  {
    title: "ASL Instructor",
    description:
      "Teach American Sign Language to learners of all levels using interactive lessons and culturally rich content to promote effective communication.",
    like:
      "leading engaging workshops, creating dynamic lesson plans, building community through sign language",
    salary: "₹28,000/month median salary",
    jobs: "10,000+ job opportunities",
    credentials: ["ASL Proficiency Certificate", "Teaching Experience"],
    image: "asl-instructor.png",
    color: "#8E44AD", // A vibrant purple
  },
  {
    title: "Community Advocate",
    description:
      "Champion accessibility and Deaf culture by engaging in community outreach, influencing policy, and organizing inclusive events.",
    like:
      "community engagement, advocacy for inclusive policies, organizing cultural events",
    salary: "₹25,000/month median salary",
    jobs: "5,000+ job opportunities",
    credentials: ["Certificate in Deaf Culture Studies", "Community Engagement Experience"],
    image: "community-advocate.png",
    color: "#FFB400", // A bright amber-yellow
  },
];

const AllCareersPage = () => {
  return (
    <div className="all-careers-page">
      <h2>Explore Careers in Sign Language</h2>
      <div className="career-cards">
        {careers.map((career, index) => (
          <div
            className="career-card"
            key={index}
            style={{ borderColor: career.color }}
          >
            <div
              className="career-card-header"
              style={{ backgroundColor: career.color }}
            >
              <img
                src={`/images/${career.image}`}
                alt={career.title}
                className="career-card-image"
              />
            </div>
            <div className="career-card-body">
              <h3 className="career-card-title">{career.title}</h3>
              <p className="career-card-description">{career.description}</p>
              <p className="career-card-like">
                <strong>If you like:</strong> {career.like}
              </p>
              {career.salary && (
                <p className="career-card-salary">
                  <strong>{career.salary}</strong>
                </p>
              )}
              {career.jobs && (
                <p className="career-card-jobs">
                  <strong>{career.jobs}</strong>
                </p>
              )}
              <div className="career-card-credentials">
                <strong>Credentials:</strong>
                <ul>
                  {career.credentials.map((cred, i) => (
                    <li key={i}>{cred}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllCareersPage;
