// src/pages/LessonPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import LessonList from "../../components/Lessons/LessonList";
import LessonDetail from "../../components/Lessons/LessonDetail";
import "../../styles/LessonPage.cs";

const LessonPage = () => {
  const [lessons, setLessons] = useState([]);
  const [selectedLesson, setSelectedLesson] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [theme, setTheme] = useState("light");

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const res = await axios.get("http://localhost:3006/api/lessons");
        setLessons(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Error fetching lessons");
      } finally {
        setLoading(false);
      }
    };
    fetchLessons();
  }, []);

  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  return (
    <div className={`lesson-page-container ${theme}`}>
      <header className="lesson-header">
        <h1>Sign Language Lessons</h1>
        <div className="theme-toggle-container">
          <select onChange={handleThemeChange} value={theme} className="theme-dropdown">
            <option value="light">â˜€ Light Mode</option>
            <option value="dark">ðŸŒ™ Dark Mode</option>
          </select>
        </div>
      </header>

      {loading && <div className="loading">Loading lessons...</div>}
      {error && <div className="error-message">{error}</div>}

      <div className="lesson-content">
        <div className="lesson-list-panel">
          <LessonList lessons={lessons} onSelectLesson={setSelectedLesson} />
        </div>
        <div className="lesson-detail-panel">
          {selectedLesson ? (
            <LessonDetail lesson={selectedLesson} onBack={() => setSelectedLesson(null)} />
          ) : (
            <div className="placeholder">
              <p>Select a lesson from the list to see details.</p>
            </div>
          )}
        </div>
      </div>

      <footer className="lesson-footer">
        <p>Â© 2025 Sign Language Platform. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default LessonPage;
