// src/pages/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../../styles/HomePage.css';

const HomePage = () => {
  // For example, after login you might store user data in localStorage.
  // This is a simple simulation. In a real app, you'd get this from a context or API.
  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <div className="home-page">
      <header>
        <h1>Welcome to ActAware</h1>
        <nav>
          {user ? (
            <div className="user-profile">
              <p>Hello, {user.name}</p>
              <p>{user.email}</p>
            </div>
          ) : (
            <div>
              <Link to="/login">Login</Link> | <Link to="/register">Register</Link>
            </div>
          )}
        </nav>
      </header>
      <main>
        <p>This is the home page content. Explore our sign language lessons and interactive features.</p>
      </main>
    </div>
  );
};

export default HomePage;
