// src/pages/UserProfile.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../..styles/UserProfile.css';

const UserProfile = () => {
  const storedUser = localStorage.getItem("user");
  const [user, setUser] = useState(storedUser ? JSON.parse(storedUser) : null);
  const [profileImage, setProfileImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [uploadError, setUploadError] = useState("");
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    // Optionally, fetch updated user data from your backend
  }, []);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!profileImage) return;
    setUploading(true);
    setUploadError("");
    try {
      const formData = new FormData();
      formData.append("profileImage", profileImage);
      formData.append("userId", user._id); // Pass the user ID

      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:5000/api/users/uploadProfile", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`
        }
      });

      const updatedUser = response.data.user;
      setUser(updatedUser);
      localStorage.setItem("user", JSON.stringify(updatedUser));
      alert("Profile image updated successfully!");
    } catch (err) {
      console.error(err);
      setUploadError(err.response?.data?.message || "Upload failed");
    } finally {
      setUploading(false);
    }
  };

  if (!user) {
    return <div>Please log in to see your profile.</div>;
  }

  return (
    <div className="user-profile">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div className="profile-image">
          <img
            src={user.profileImage || previewImage || "/assets/default-profile.png"}
            alt="Profile"
          />
          <input type="file" accept="image/*" onChange={handleImageChange} />
          {profileImage && (
            <button onClick={handleUpload} disabled={uploading}>
              {uploading ? "Uploading..." : "Upload New Image"}
            </button>
          )}
          {uploadError && <p className="error">{uploadError}</p>}
        </div>
        <div className="profile-info">
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
