// src/pages/RegisterPage.js
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { registerUser } from "../../services/authService";
import "../../styles/RegisterForm.css";

const RegisterPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    hearingStatus: "hearing",
    reasonForLearning: "",
    skillLevel: "",
    phoneNumber: "",
    country: "",
  });
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const data = await registerUser(formData);
      // data = { message, user, token? }
      alert(data.message); // e.g. "User registered successfully"

      // Store user (and token if provided) in localStorage
      if (data.user) {
        localStorage.setItem("user", JSON.stringify(data.user));
      }
      if (data.token) {
        localStorage.setItem("token", data.token);
      }

      // Redirect to homepage
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.message || "Registration failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-page">
      {/* Left Column */}
      <div className="register-left">
        <div className="register-header">
          <h1 className="logo">ActAware</h1>
          <h2>Create Your Account</h2>
          <p>Please fill in your details below</p>
        </div>

        <form onSubmit={handleSubmit} className="register-form">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Enter your name"
            value={formData.name}
            onChange={handleChange}
            required
          />

          <label htmlFor="email">Email</label>
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Enter your email"
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label htmlFor="password">Password</label>
          <input
            type="password"
            name="password"
            id="password"
            placeholder="Enter your password"
            value={formData.password}
            onChange={handleChange}
            required
          />

          {/* Extra Fields */}
          <label htmlFor="hearingStatus">Hearing Status</label>
          <select
            name="hearingStatus"
            id="hearingStatus"
            value={formData.hearingStatus}
            onChange={handleChange}
          >
            <option value="hearing">Hearing</option>
            <option value="hard_of_hearing">Hard of Hearing</option>
            <option value="deaf">Deaf</option>
          </select>

          <label htmlFor="reasonForLearning">Reason for Learning</label>
          <textarea
            id="reasonForLearning"
            name="reasonForLearning"
            placeholder="Tell us why you're interested in sign language..."
            value={formData.reasonForLearning}
            onChange={handleChange}
          />

          <label htmlFor="skillLevel">Skill Level</label>
          <select
            name="skillLevel"
            id="skillLevel"
            value={formData.skillLevel}
            onChange={handleChange}
          >
            <option value="">Select your skill level</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>

          <label htmlFor="phoneNumber">Phone Number</label>
          <input
            type="tel"
            name="phoneNumber"
            id="phoneNumber"
            placeholder="Enter phone number"
            value={formData.phoneNumber}
            onChange={handleChange}
          />

          <label htmlFor="country">Country</label>
          <input
            type="text"
            name="country"
            id="country"
            placeholder="Enter your country"
            value={formData.country}
            onChange={handleChange}
          />

          {error && <p className="error">{error}</p>}

          <button type="submit" className="register-btn" disabled={loading}>
            {loading ? "Registering..." : "Sign Up"}
          </button>

          <div className="login-link">
            <p>
              Already have an account? <Link to="/login">Sign In</Link>
            </p>
          </div>
        </form>
      </div>

      {/* Right Column */}
      <div className="register-right">
        <div className="illustration-text">{/* Additional content */}</div>
      </div>
    </div>
  );
};

export default RegisterPage;
