// src/pages/HomePage.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../"; // We'll create this CSS next

const HomePage = () => {
  // State to manage theme (light or night)
  const [theme, setTheme] = useState("light");

  // Handle theme changes
  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  return (
    <div className={`homepage ${theme}`}>
      {/* Sidebar */}
      <aside className="sidebar">
        <button className="sidebar-toggle">☰</button>
        <nav className="nav-links">
          <Link to="/">HomePage</Link>
          <Link to="/lessons">Lesson</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/profile">UserProfile</Link>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
        </nav>

        <div className="theme-dropdown">
          <label htmlFor="theme-select">Change Mode:</label>
          <select id="theme-select" value={theme} onChange={handleThemeChange}>
            <option value="light">Light Mode</option>
            <option value="night">Night Mode</option>
          </select>
        </div>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        <h1>Welcome to Our Website</h1>
        <p>Manage sign language lessons, quizzes, and more.</p>

        {/* Image Scroller for About Section */}
        <section className="about-section">
          <h2>About Our Website</h2>
          <div className="image-scroller">
            <div className="scroller-container">
              {/* Replace these with your own images and paths */}
              <img src="/assets/image1.jpg" alt="About 1" />
              <img src="/assets/image2.jpg" alt="About 2" />
              <img src="/assets/image3.jpg" alt="About 3" />
              <img src="/assets/image4.jpg" alt="About 4" />
            </div>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>© 2025 Sign Language Platform. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
