import React from "react";
import { Link } from "react-router-dom";
import "../../styles/Footer.css";

const SignLanguageFooter = ({ darkMode }) => {
  return (
    <footer className={`sign-lang-footer ${darkMode ? "dark" : ""}`}>
      {/* Wave background at the top of the footer */}
      <div className="footer-wave"></div>

      <div className="footer-content">
        {/* Column 1: Quick links */}
        <div className="footer-column">
          <h3>Hey, here's some more stuff</h3>
          <ul>
            <li><Link to="/contact">Contact</Link></li>
            <li><Link to="/about">About Us</Link></li>
            <li><Link to="/blog">Blog</Link></li>
            <li><Link to="/terms">Terms of Use</Link></li>
          </ul>
        </div>

        {/* Column 2: Newsletter or Email Signup */}
        <div className="footer-column">
          <h3>We send fun emails!</h3>
          <p>Get a 20% discount when you sign up! Receive sign language updates, new features, and more.</p>
          <div className="newsletter-form">
            <input type="email" placeholder="Email address here, please!" />
            <button>SIGN UP</button>
          </div>
        </div>

        {/* Column 3: Socials or Info */}
        <div className="footer-column">
          <h3>Follow Our Sign Language Journey</h3>
          <div className="social-icons">
            <a href="https://facebook.com" target="_blank" rel="noreferrer">FB</a>
            <a href="https://twitter.com" target="_blank" rel="noreferrer">TW</a>
            <a href="https://instagram.com" target="_blank" rel="noreferrer">IG</a>
            {/* Add more icons as needed */}
          </div>
          <p>Here's the GIF of the week!</p>
          <img src="/assets/gif-of-week.gif" alt="Sign Language GIF" className="gif-of-week" />
        </div>
      </div>

      {/* Bottom line for copyright */}
      <div className="footer-bottom">
        <p>© 2025 Sign Language Platform. All rights reserved.</p>
        <Link to="/terms">Terms & Conditions</Link>
      </div>
    </footer>
  );
};

export default SignLanguageFooter;
