import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/AllCareersPage.css";

const careerPaths = [
  {
    id: 1,
    title: "Sign Language Interpreter",
    snippet:
      "Master essential sign language skills to bridge communication gaps in various settings.",
    credentials: ["ASL Certification", "BS in Deaf Studies"],
    imageUrl:
      "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?q=80&w=1170&auto=format&fit=crop",
    jobInfo: { averageSalary: "₹30,000/mo", openings: "12,000+ openings" },
    details:
      "Sign Language Interpreters play a crucial role in enabling effective communication for the Deaf community. They work in schools, hospitals, and workplaces, ensuring accessibility and inclusion.",
  },
  {
    id: 2,
    title: "Deaf Educator",
    snippet:
      "Help Deaf students excel academically through advanced sign language teaching methods.",
    credentials: ["MA in Deaf Education", "TESOL for ASL"],
    imageUrl:
      "https://plus.unsplash.com/premium_photo-1689708721750-8a0e6dc14cee?q=80&w=1170&auto=format&fit=crop",
    jobInfo: { averageSalary: "₹35,000/mo", openings: "8,000+ openings" },
    details:
      "Deaf Educators specialize in teaching students who are Deaf or Hard of Hearing. They use visual learning techniques and adaptive teaching strategies to create an inclusive learning environment.",
  },
  {
    id: 3,
    title: "Speech-Language Pathologist",
    snippet:
      "Combine speech therapy with sign language to help children and adults communicate effectively.",
    credentials: ["Speech Pathology MS", "ASL Proficiency"],
    imageUrl:
      "https://plus.unsplash.com/premium_photo-1688350808212-4e6908a03925?q=80&w=1169&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    jobInfo: { averageSalary: "₹40,000/mo", openings: "10,000+ openings" },
    details:
      "Speech-Language Pathologists help individuals with speech disorders develop communication skills. They work with ASL to support patients who rely on sign language for expression.",
  },
  {
    id: 4,
    title: "Community Advocate",
    snippet:
      "Advocate for accessibility, Deaf culture, and inclusive communication strategies.",
    credentials: ["Deaf Culture Studies", "Nonprofit Experience"],
    imageUrl:
      "https://images.unsplash.com/photo-1651684215020-f7a5b6610f23?q=80&w=1169&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    jobInfo: { averageSalary: "₹28,000/mo", openings: "5,000+ openings" },
    details:
      "Community Advocates work to improve policies and raise awareness for the Deaf community. They collaborate with organizations to promote accessibility and inclusivity in society.",
  },
];

const AllCareersPage = () => {
  const [expanded, setExpanded] = useState({});

  const toggleReadMore = (id) => {
    setExpanded((prevState) => ({
      ...prevState,
      [id]: !prevState[id],
    }));
  };

  return (
    <div className="all-careers-page">
      <h2>Explore All Career Paths</h2>
      <div className="career-cards">
        {careerPaths.map((path) => (
          <div className="career-card" key={path.id}>
            <img className="career-card-image" src={path.imageUrl} alt={path.title} />
            <h3 className="career-card-title">{path.title}</h3>
            <p className="career-card-snippet">{path.snippet}</p>
            <p className="career-card-credentials">
              <strong>Credentials:</strong> {path.credentials.join(", ")}
            </p>
            <p className="career-card-jobinfo">
              <strong>Avg. Salary:</strong> {path.jobInfo.averageSalary} <br />
              <strong>Openings:</strong> {path.jobInfo.openings}
            </p>
            {expanded[path.id] && <p className="career-card-details">{path.details}</p>}
            <button className="read-more-btn" onClick={() => toggleReadMore(path.id)}>
              {expanded[path.id] ? "Read Less" : "Read More"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AllCareersPage;
