// src/components/LessonCard.js
import React from "react";
import "../../styles/LessonCard.css";

const LessonCard = ({ lesson, onClick }) => {
  return (
    <div className="lesson-card" onClick={onClick}>
      {lesson.videoURL ? (
        <div className="lesson-thumbnail">
          <img src={lesson.videoURL} alt={lesson.title} />
          <div className="play-overlay">â–¶</div>
        </div>
      ) : (
        <div className="lesson-thumbnail no-video">
          <span>No Video</span>
        </div>
      )}
      <div className="lesson-info">
        <h2 className="lesson-title">{lesson.title}</h2>
        <p className="lesson-description">
          {lesson.description
            ? lesson.description.substring(0, 100) + "..."
            : "No description available."}
        </p>
        <div className="lesson-meta">
          <span className="lesson-category">{lesson.category}</span>
          <span className="lesson-difficulty">{lesson.difficulty}</span>
        </div>
        {lesson.instructor && (
          <p className="lesson-instructor">
            Instructor: {lesson.instructor.name}
          </p>
        )}
        <button className="view-button">View Details</button>
      </div>
    </div>
  );
};

export default LessonCard;
