// src/pages/LessonsPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import LessonCard from "../components/Le";
import "../../styles/LessonsPage.css";

const LessonsPage = () => {
  const [lessons, setLessons] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchLessons = async () => {
      try {
        const res = await axios.get("http://localhost:3006/api/lessons");
        setLessons(res.data);
      } catch (err) {
        setError(err.response?.data?.message || "Error fetching lessons");
      } finally {
        setLoading(false);
      }
    };
    fetchLessons();
  }, []);

  if (loading) {
    return <div className="lessons-loading">Loading lessons...</div>;
  }

  if (error) {
    return <div className="lessons-error">{error}</div>;
  }

  return (
    <div className="lessons-page-container">
      <h1 className="page-title">Our Lessons</h1>
      <div className="lessons-grid">
        {lessons.map((lesson) => (
          <LessonCard key={lesson._id} lesson={lesson} />
        ))}
      </div>
    </div>
  );
};

export default LessonsPage;
