// src/components/LessonList.js
import React from "react";
import LessonCard from "./LessonCar";
import "../styles/LessonList.css";

const LessonList = ({ lessons, onSelectLesson }) => {
  return (
    <div className="lesson-list">
      {lessons.map((lesson) => (
        <LessonCard
          key={lesson._id}
          lesson={lesson}
          onClick={() => onSelectLesson(lesson)}
        />
      ))}
    </div>
  );
};

export default LessonList;
