// src/pages/HomePage.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styl";

const HomePage = () => {
  // Local theme state for toggling light/dark mode
  const [theme, setTheme] = useState("light");

  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  return (
    <div className={`homepage-container ${theme}`}>
      {/* NAVBAR */}
      <header className="navbar">
        <div className="logo-section">
          <img src="/assets/logo.png" alt="Site Logo" className="site-logo" />
          <span className="site-name">Sign Language Platform</span>
        </div>

        <nav className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/lesson">Lesson</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/userprofile">UserProfile</Link>
          <Link to="/about">About</Link>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
        </nav>

        {/* Light/Dark Mode Dropdown */}
        <div className="theme-dropdown">
          <select onChange={handleThemeChange} value={theme}>
            <option value="light">â˜€ Light Mode</option>
            <option value="dark">ðŸŒ™ Dark Mode</option>
          </select>
        </div>
      </header>

      {/* HERO SECTION */}
      <section className="hero-section">
        <div className="hero-text">
          <h1>
            Put your message into orbit with
            <br />
            ANIMATED VIDEO
          </h1>
          <p>
            Take your sign language learning to new heights with interactive lessons
            and immersive quizzes.
          </p>
          <button className="watch-btn">Watch video</button>
        </div>

        <div className="hero-image">
          {/* Replace astronaut.png with your relevant image */}
          <img src="/assets/astronaut.png" alt="Astronaut" />
        </div>

        {/* Scroll Down Indicator */}
        <div className="scroll-down">Scroll down</div>
      </section>

      {/* FOOTER */}
      <footer className="footer">
        <p>Â© 2025 Sign Language Platform. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
