// src/pages/UserProfile.js
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../styles/UserProfile.css";

const UserProfile = () => {
  const navigate = useNavigate();
  const storedUser = localStorage.getItem("user");
  const [user, setUser] = useState(storedUser ? JSON.parse(storedUser) : null);
  const [profileImage, setProfileImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState("");

  useEffect(() => {
    // Optionally, fetch latest user data from the backend if needed.
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!profileImage) return;
    setUploading(true);
    setUploadError("");
    try {
      // Prepare form data for file upload
      const formData = new FormData();
      formData.append("profileImage", profileImage);
      formData.append("userId", user._id);
      // Adjust the URL to match your backend endpoint for profile image upload.
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:3006/api/users/uploadProfile", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`
        }
      });
      // Assume the backend returns the updated user data with a new profileImage URL.
      const updatedUser = response.data.user;
      setUser(updatedUser);
      localStorage.setItem("user", JSON.stringify(updatedUser));
      alert("Profile updated successfully!");
    } catch (err) {
      console.error(err);
      setUploadError(err.response?.data?.message || "Upload failed");
    } finally {
      setUploading(false);
    }
  };

  if (!user) {
    return <div>No user data. Please log in.</div>;
  }

  return (
    <div className="user-profile">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div className="profile-image">
          <img
            src={
              user.profileImage ||
              previewImage ||
              "/assets/default-profile.png"
            }
            alt="Profile"
          />
          <br></br>
          <input type="file" accept="image/*" onChange={handleImageChange} />
          {profileImage && (
            <button onClick={handleUpload} disabled={uploading}>
              {uploading ? "Uploading..." : "Upload New Image"}
            </button>
          )}
          {uploadError && <p className="error">{uploadError}</p>}
        </div>
        <div className="profile-info">
          <p>
            <strong>Name:</strong> {user.name}
          </p>
          <p>
            <strong>Email:</strong> {user.email}
          </p>
          <p>
            <strong>Role:</strong> {user.role}
          </p>
          <p>
            <strong>Em:</strong> {user.email}
          </p>
          {/* You can display more user details here */}
        </div>
      </div>
      <button onClick={handleLogout} className="logout-button">
        Logout
      </button>
    </div>
  );
};

export default UserProfile;
