// src/components/LessonDetail.js
import React from "react";
import "../.styles/LessonDetail.css";

const LessonDetail = ({ lesson, onBack }) => {
  return (
    <div className="lesson-detail">
      <button className="back-button" onClick={onBack}>‚Üê Back to Lessons</button>
      <h1 className="lesson-title">{lesson.title}</h1>
      <p className="category-difficulty">
        <span className="lesson-category">{lesson.category}</span> |{" "}
        <span className="lesson-difficulty">{lesson.difficulty}</span>
      </p>
      {lesson.videoURL && (
        <div className="video-wrapper">
          <video controls poster="/assets/video-placeholder.jpg">
            <source src={lesson.videoURL} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </div>
      )}
      <p className="lesson-description-full">{lesson.description}</p>
      {lesson.instructor && (
        <p className="instructor-info">
          Instructor: {lesson.instructor.name} ({lesson.instructor.email})
        </p>
      )}
    </div>
  );
};

export default LessonDetail;
