// src/pages/UserProfile.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../../styles/UserProfile.css";

const UserProfile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (stored) {
      setUser(JSON.parse(stored));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (!user) return <div>Please log in to view your profile.</div>;

  return (
    <div className="user-profile">
      <h2>Welcome, {user.name}!</h2>
      <div className="profile-info">
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Hearing Status:</strong> {user.hearingStatus}</p>
        <p><strong>Reason for Learning:</strong> {user.reasonForLearning}</p>
        <p><strong>Skill Level:</strong> {user.skillLevel}</p>
        <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
        <p><strong>Country:</strong> {user.country}</p>
      </div>
      <div className="profile-actions">
        <button onClick={handleLogout}>Logout</button>
        <Link to={`/profile-onboarding/${user._id}`} className="edit-profile-link">
          Edit Profile
        </Link>
      </div>
    </div>
  );
};

export default UserProfile;
