// src/pages/ProfileUpdate.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import '../../styles/ProfileOnboarding.css';

const ProfileUpdate = () => {
  const { userId } = useParams();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    hearingStatus: 'hearing',
    reasonForLearning: '',
    skillLevel: '',
    phoneNumber: '',
    country: ''
  });
  const [profileImage, setProfileImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Optionally, fetch existing user data
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const res = await axios.get(`http://localhost:5/api/users/${userId}`);
        const user = res.data.user;
        setFormData({
          hearingStatus: user.hearingStatus || 'hearing',
          reasonForLearning: user.reasonForLearning || '',
          skillLevel: user.skillLevel || '',
          phoneNumber: user.phoneNumber || '',
          country: user.country || ''
        });
      } catch (err) {
        console.error(err);
      }
    };
    fetchUserData();
  }, [userId]);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = e => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      const dataToSend = new FormData();
      dataToSend.append('hearingStatus', formData.hearingStatus);
      dataToSend.append('reasonForLearning', formData.reasonForLearning);
      dataToSend.append('skillLevel', formData.skillLevel);
      dataToSend.append('phoneNumber', formData.phoneNumber);
      dataToSend.append('country', formData.country);
      if (profileImage) {
        dataToSend.append('profileImage', profileImage);
      }
      
      const token = localStorage.getItem('token');
      const res = await axios.patch(`http://localhost:5000/api/users/profile/${userId}`, dataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        }
      });
      
      alert(res.data.message);
      // Update user details locally if needed
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/profile'); // Redirect to the profile view page
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-update">
      <h2>Complete Your Profile</h2>
      <p>Please update your profile with additional details.</p>
      <form onSubmit={handleSubmit}>
        <label htmlFor="hearingStatus">Hearing Status</label>
        <select name="hearingStatus" id="hearingStatus" value={formData.hearingStatus} onChange={handleChange}>
          <option value="hearing">Hearing</option>
          <option value="hard_of_hearing">Hard of Hearing</option>
          <option value="deaf">Deaf</option>
        </select>

        <label htmlFor="reasonForLearning">Reason for Learning</label>
        <textarea
          id="reasonForLearning"
          name="reasonForLearning"
          placeholder="Tell us why you're interested in sign language..."
          value={formData.reasonForLearning}
          onChange={handleChange}
        />

        <label htmlFor="skillLevel">Skill Level</label>
        <select name="skillLevel" id="skillLevel" value={formData.skillLevel} onChange={handleChange}>
          <option value="">Select your skill level</option>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>

        <label htmlFor="phoneNumber">Phone Number</label>
        <input
          type="tel"
          id="phoneNumber"
          name="phoneNumber"
          placeholder="Enter phone number"
          value={formData.phoneNumber}
          onChange={handleChange}
        />

        <label htmlFor="country">Country</label>
        <input
          type="text"
          id="country"
          name="country"
          placeholder="Enter your country"
          value={formData.country}
          onChange={handleChange}
        />

        <label htmlFor="profileImage">Profile Image</label>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        {previewImage && (
          <img src={previewImage} alt="Profile Preview" style={{ width: '150px', marginTop: '10px' }} />
        )}

        {error && <p className="error">{error}</p>}
        <button type="submit" disabled={loading}>
          {loading ? 'Updating...' : 'Update Profile'}
        </button>
      </form>
    </div>
  );
};

export default ProfileUpdate;
