// src/pages/UserProfile.js
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../../styles/UserProfile.css';

const UserProfile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(() => {
    // Initialize state from localStorage
    const stored = localStorage.getItem("user");
    return stored ? JSON.parse(stored) : null;
  });

  // Optionally, refresh user from localStorage when component mounts
  useEffect(() => {
    const stored = localStorage.getItem("user");
    if (stored) {
      setUser(JSON.parse(stored));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (!user) {
    return <div>Please log in to view your profile.</div>;
  }

  return (
    <div className="user-profile">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div className="profile-image">
          <img 
            src={user.profileImage || "/assets/default-profile.png"} 
            alt="User Profile" 
          />
        </div>
        <div className="profile-info">
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
          {/* Additional details */}
          <p><strong>Hearing Status:</strong> {user.hearingStatus}</p>
          <p><strong>Reason for Learning:</strong> {user.reasonForLearning}</p>
          <p><strong>Skill Level:</strong> {user.skillLevel}</p>
          <p><strong>Phone Number:</strong> {user.phoneNumber}</p>
          <p><strong>Country:</strong> {user.country}</p>
        </div>
      </div>
      <div className="profile-actions">
        <button onClick={handleLogout} className="logout-button">Logout</button>
        <Link to={`/profile/update/${user._id}`} className="update-link">
          Update Profile
        </Link>
      </div>
    </div>
  );
};

export default UserProfile;
