// src/components/LessonCard.js
import React from "react";
import "../../styles/lessons.css";

const LessonCard = ({ lesson, onClick }) => {
  return (
    <div className="lesson-card" onClick={onClick}>
      <h2>{lesson.title}</h2>
      <p className="category">{lesson.category} | {lesson.difficulty}</p>
      <p className="description">
        {lesson.description ? lesson.description.substring(0, 100) + "..." : "No description available."}
      </p>
      <button className="view-button">View Details</button>
    </div>
  );
};

export default LessonCard;
