// src/pages/ContactPage.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/ContactPage.cs";

const ContactPage = () => {
  const [theme, setTheme] = useState("light");

  const handleToggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  return (
    <div className={`contact-page ${theme}`}>
      <header className="contact-header">
        <h1>Contact us</h1>
        <button className="theme-btn" onClick={handleToggleTheme}>
          {theme === "light" ? "Dark Mode" : "Light Mode"}
        </button>
      </header>

      <div className="contact-container">
        {/* Left Card: Sales Team */}
        <div className="contact-card">
          <div className="contact-icon">üìû</div>
          <h2>Talk to a member of our Sales team</h2>
          <p>
            We will help you find the right products and pricing
            for your business.
          </p>
          <Link to="/lessons" className="contact-action">
            Contact Sales
          </Link>
        </div>

        {/* Right Card: Product/Account Support */}
        <div className="contact-card">
          <div className="contact-icon">üìù</div>
          <h2>Product and account support</h2>
          <p>
            Our help centre is fresh and always open for business.
            If you can‚Äôt find the answer you are looking for,
            we are here to lend a hand.
          </p>
          <Link to="/lessons" className="contact-action">
            Go to the help centre
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ContactPage;
