// src/pages/ProfileForm.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/ProfileForm.css";

const ProfileForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: "",
    age: "",
    status: "",
    location: "",
    hearingStatus: "hearing",
    reasonForLearning: "",
    skillLevel: "beginner",
    favoriteBrands: "",
    motivations: { accessibility: 50, learningSpeed: 70, community: 80 },
    frustrations: { complexSigns: true, fewResources: true, noPractice: false },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    localStorage.setItem("signLanguageUser", JSON.stringify(formData));
    navigate("/user-profile");
  };

  return (
    <div className="profile-form">
      <h2>Create Your Sign Language Profile</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />

        <label>Age:</label>
        <input type="number" name="age" value={formData.age} onChange={handleChange} required />

        <label>Status:</label>
        <input type="text" name="status" value={formData.status} onChange={handleChange} required />

        <label>Location:</label>
        <input type="text" name="location" value={formData.location} onChange={handleChange} required />

        <label>Hearing Status:</label>
        <select name="hearingStatus" value={formData.hearingStatus} onChange={handleChange}>
          <option value="hearing">Hearing</option>
          <option value="hard_of_hearing">Hard of Hearing</option>
          <option value="deaf">Deaf</option>
        </select>

        <label>Reason for Learning Sign Language:</label>
        <textarea name="reasonForLearning" value={formData.reasonForLearning} onChange={handleChange} required />

        <label>Skill Level:</label>
        <select name="skillLevel" value={formData.skillLevel} onChange={handleChange}>
          <option value="beginner">Beginner</option>
          <option value="intermediate">Intermediate</option>
          <option value="advanced">Advanced</option>
        </select>

        <label>Favorite Brands (Comma separated):</label>
        <input type="text" name="favoriteBrands" value={formData.favoriteBrands} onChange={handleChange} />

        <button type="submit">Create Profile</button>
      </form>
    </div>
  );
};

export default ProfileForm;
