// src/pages/SignLangQuizCategories.js
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/SignLangQuizCategories.css";

const SignLangQuizCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Fetch categories from your backend
    // Adjust the URL & response shape to match your API
    fetch("http://localhost:3006/api/quiz/signlang-categories")
      .then((res) => res.json())
      .then((data) => {
        // e.g., data might be { categories: [{_id, name, iconUrl}, ...] }
        setCategories(data.categories || []);
      })
      .catch((err) => console.error("Error fetching categories:", err));
  }, []);

  return (
    <div className="quiz-categories-page">
      <header className="quiz-header">
        <h1>Choose a Sign Language Quiz Topic</h1>
        <p>Select a topic to test your knowledge</p>
      </header>

      <div className="categories-grid">
        {categories.map((cat) => (
          <Link
            key={cat._id}
            to={`/quiz/${cat._id}`} // Navigates to e.g. /quiz/alphabet
            className="category-box"
          >
            <img
              src={cat.iconUrl || "/assets/default-icon.png"}
              alt={cat.name}
              className="category-icon"
            />
            <span>{cat.name}</span>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SignLangQuizCategories;
