import React from 'react';
import { Link } from 'react-router-dom';
import './HomeP'; // Optional: Create a CSS file for styling

const HomePage = () => {
  return (
    <div className="home-page">
      <h1>Welcome to ActAware</h1>
      <p>Learn sign language with our interactive platform. Explore lessons, take quizzes, and join our community.</p>
      <div className="home-links">
        <Link className="home-link" to="/lessons">View Lessons</Link>
        <Link className="home-link" to="/quiz">Take a Quiz</Link>
        <Link className="home-link" to="/login">Login</Link>
        <Link className="home-link" to="/register">Register</Link>
      </div>
    </div>
  );
};

export default HomePage;
