// src/pages/UserProfile.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../.styles/UserProfile.css";

const UserProfile = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("signLanguageUser");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  if (!user) return <div>Loading...</div>;

  return (
    <div className="user-profile">
      <div className="profile-header">
        <img src="https://via.placeholder.com/150" alt="User" className="profile-img" />
        <h1>{user.name}</h1>
        <p>{user.status}</p>
      </div>

      <div className="profile-section">
        <h2>Bio</h2>
        <p>{user.reasonForLearning}</p>
      </div>

      <div className="profile-section">
        <h2>Hearing Status</h2>
        <p>{user.hearingStatus}</p>
      </div>

      <div className="profile-section">
        <h2>Skill Level</h2>
        <p>{user.skillLevel}</p>
      </div>

      <div className="profile-section">
        <h2>Motivations</h2>
        <p>Accessibility: {user.motivations.accessibility}%</p>
        <p>Learning Speed: {user.motivations.learningSpeed}%</p>
        <p>Community: {user.motivations.community}%</p>
      </div>

      <div className="profile-section">
        <h2>Frustrations</h2>
        {user.frustrations.complexSigns && <p>Struggles with complex signs</p>}
        {user.frustrations.fewResources && <p>Limited learning resources</p>}
        {user.frustrations.noPractice && <p>Difficulty finding practice partners</p>}
      </div>

      <div className="profile-section">
        <h2>Favorite Brands</h2>
        <p>{user.favoriteBrands}</p>
      </div>

      <button onClick={() => navigate("/profile-form")}>Edit Profile</button>
    </div>
  );
};

export default UserProfile;
