// src/components/LessonSteps.js
import React, { useState } from 'react';
import '../../styles/LessonSteps.css';

const steps = [
  {
    image: '/Frontend/public/assets/Descargar comunicarse con lenguaje de señas gratis.jpeg',
    text: 'Step 1: Watch the introduction video to get started.'
  },
  {
    image: '/Frontend/public/assets/Download Deaf People Communicating Through Sign Language for free.jpeg',
    text: 'Step 2: Practice with interactive exercises.'
  },
  {
    image: '../../assets/Des',
    text: 'Step 3: Test your knowledge with quizzes.'
  }
];

const LessonSteps = () => {
  const [currentStep, setCurrentStep] = useState(0);

  const handleNext = () => {
    setCurrentStep((currentStep + 1) % steps.length);
  };

  return (
    <div className="lesson-steps">
      <img src={steps[currentStep].image} alt={`Step ${currentStep + 1}`} />
      <p>{steps[currentStep].text}</p>
      <button onClick={handleNext}>Next Step</button>
    </div>
  );
};

export default LessonSteps;
