// src/pages/LessonPage.js
import React from "react";
import "../../styles";

const LessonPage = () => {
  // Sample data to display in the cards (replace with your actual data or fetch from backend)
  const signLanguagePaths = [
    {
      id: 1,
      title: "Sign Language Interpreter",
      snippet:
        "Master essential sign language skills to help bridge communication gaps. Assist the Deaf community in hospitals, courts, and beyond.",
      credentials: ["ASL Certification", "BS in Deaf Studies"],
      imageUrl: "/assets/interpreter.jpg",
      jobInfo: {
        averageSalary: "₹30,000/mo",
        openings: "12,000+ openings",
      },
      linkText: "View all Interpreter paths",
      linkUrl: "/lesson/interpreter",
    },
    {
      id: 2,
      title: "Deaf Educator",
      snippet:
        "Learn advanced sign language teaching methods to help Deaf students excel in academics and everyday life.",
      credentials: ["MA in Deaf Education", "TESOL for ASL"],
      imageUrl: "/assets/deaf-educator.jpg",
      jobInfo: {
        averageSalary: "₹35,000/mo",
        openings: "8,000+ openings",
      },
      linkText: "View Deaf Educator programs",
      linkUrl: "/lesson/deaf-educator",
    },
    {
      id: 3,
      title: "Speech-Language Pathologist",
      snippet:
        "Combine speech therapy with sign language to empower children and adults to communicate effectively in multiple modalities.",
      credentials: ["Speech Pathology MS", "ASL Proficiency"],
      imageUrl: "/assets/slp.jpg",
      jobInfo: {
        averageSalary: "₹40,000/mo",
        openings: "10,000+ openings",
      },
      linkText: "Explore SLP roles",
      linkUrl: "/lesson/slp",
    },
    {
      id: 4,
      title: "Community Advocate",
      snippet:
        "Advocate for accessibility, Deaf culture, and inclusive communication strategies in various community settings.",
      credentials: ["Deaf Culture Studies", "Nonprofit Experience"],
      imageUrl: "/assets/advocate.jpg",
      jobInfo: {
        averageSalary: "₹28,000/mo",
        openings: "5,000+ openings",
      },
      linkText: "Browse Advocacy roles",
      linkUrl: "/lesson/advocate",
    },
  ];

  return (
    <div className="sign-language-lesson-page">
      {/* Page Header */}
      <header className="lesson-page-header">
        <nav className="lesson-page-nav">
          <h1>Sign Language Platform</h1>
          <ul>
            <li>Home</li>
            <li>Lessons</li>
            <li>Quiz</li>
            <li>UserProfile</li>
            <li>About</li>
            <li>Register</li>
            <li>Login</li>
            <li>Contact Us</li>
          </ul>
        </nav>
      </header>

      {/* Subtitle */}
      <section className="lesson-subtitle">
        <h2>Because you want to start a career in Sign Language</h2>
        <p>
          Unlock professional opportunities by mastering sign language paths
          designed to help you thrive in diverse roles.
        </p>
      </section>

      {/* Card Row */}
      <section className="career-paths-section">
        <div className="career-cards">
          {signLanguagePaths.map((path) => (
            <div className="career-card" key={path.id}>
              <div className="career-card-image">
                <img src={path.imageUrl} alt={path.title} />
              </div>
              <h3 className="career-card-title">{path.title}</h3>
              <p className="career-card-snippet">{path.snippet}</p>

              <div className="career-card-info">
                <p className="credentials">
                  <strong>Credentials:</strong>{" "}
                  {path.credentials.join(", ")}
                </p>
                <p className="job-info">
                  <strong>Avg. Salary:</strong> {path.jobInfo.averageSalary} <br />
                  <strong>Openings:</strong> {path.jobInfo.openings}
                </p>
              </div>

              <a className="career-card-link" href={path.linkUrl}>
                {path.linkText}
              </a>
            </div>
          ))}
        </div>
        <div className="see-all-roles">
          <a href="/lesson/all">See all roles →</a>
        </div>
      </section>
    </div>
  );
};

export default LessonPage;
