// src/pages/AboutPage.js
import React from "react";
import ThemeToggle from "../../components";
import AboutIllustration from "../../components/UI/AboutIllustration";
import "../../styles/AboutPage.css";

const AboutPage = () => {
  return (
    <div className="about-container">
      {/* Theme Toggle */}
      <ThemeToggle />

      {/* Hero Section */}
      <section className="about-hero">
        <h1 className="animated-heading">Calling All Makers</h1>
        <p className="subtitle">
          We’re looking for creative innovators who share our passion for AI,
          technology, and sign language. Makers who love to build things with
          their own hands and challenge the status quo.
        </p>
        <AboutIllustration />
      </section>

      {/* Join Us Section */}
      <section className="join-section">
        <h2>Join Us</h2>
        <p>
          Ready to be part of a community that pushes boundaries, embraces
          innovation, and transforms sign language learning? We can’t wait to
          see what you’ll create.
        </p>
      </section>
    </div>
  );
};

export default AboutPage;
