// src/pages/UserProfile.js
import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../styles';

const UserProfile = () => {
  // Retrieve the user from localStorage (assumes it's been stored as JSON)
  const user = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();

  // Logout function to clear user data and redirect to login
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  if (!user) {
    return <div>Please log in to view your profile.</div>;
  }

  return (
    <div className="user-profile">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div className="profile-image">
          {/* Display the user's profile image, or a default if none is provided */}
          <img 
            src={user.profileImage || "/assets/default-profile.png"} 
            alt="User Profile" 
          />
        </div>
        <div className="profile-info">
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Role:</strong> {user.role}</p>
          {/* Additional details can be added here */}
        </div>
      </div>
      <div className="profile-actions">
        <button onClick={handleLogout} className="logout-button">Logout</button>
        <Link to={`/profile/update/${user._id}`} className="update-link">
          Update Profile
        </Link>
      </div>
    </div>
  );
};

export default UserProfile;
