// src/pages/SignLangQuizCategories.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/SignLangQuizCategories.css";

const SignLangQuizCategories = () => {
  // Hard-coded categories for demonstration
  const categories = [
    { id: "alphabet", name: "Alphabet", iconUrl: "/assets/icons/alphabet.png" },
    { id: "numbers", name: "Numbers", iconUrl: "/assets/icons/numbers.png" },
    { id: "phrases", name: "Basic Phrases", iconUrl: "/assets/icons/phrases.png" },
    { id: "grammar", name: "Grammar", iconUrl: "/assets/icons/grammar.png" },
    { id: "culture", name: "Culture", iconUrl: "/assets/icons/culture.png" },
    { id: "fingerspelling", name: "Fingerspelling", iconUrl: "/assets/icons/fingerspelling.png" },
    // Add more sign language categories as needed
  ];

  // Dark/Light mode state
  const [darkMode, setDarkMode] = useState(false);

  // Toggle the darkMode boolean
  const handleToggleTheme = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`quiz-categories-page ${darkMode ? "dark" : ""}`}>
      <header className="quiz-header">
        <h1>Test Your Knowledge!</h1>
        <p>Select a Sign Language Category</p>

        {/* Theme toggle button */}
        <button className="theme-toggle-btn" onClick={handleToggleTheme}>
          {darkMode ? "Light Mode" : "Dark Mode"}
        </button>
      </header>

      <div className="categories-grid">
        {categories.map((cat) => (
          <Link
            key={cat.id}
            to={`/quiz/${cat.id}`}
            className="category-box"
          >
            <img
              src={cat.iconUrl || "/assets/icons/default.png"}
              alt={cat.name}
              className="category-icon"
            />
            <span>{cat.name}</span>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SignLangQuizCategories;
