// src/pages/HomePage.js
import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../styles/HomePage.css";

const HomePage = () => {
  const [theme, setTheme] = useState("light");

  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  return (
    <div className={`homepage-container ${theme}`}>
      {/* Header */}
      <header className="header">
        <div className="logo-section">
          <img src="/assets/logo.png" alt="Logo" className="site-logo" />
          <span className="site-name">Sign Language Platform</span>
        </div>
        <nav className="nav-menu">
          <Link to="/">Home</Link>
          <Link to="/lessons">Lessons</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/profile">Profile</Link>
          <Link to="/register">Register</Link>
          <Link to="/login">Login</Link>
        </nav>
        <div className="theme-dropdown">
          <select onChange={handleThemeChange} value={theme}>
            <option value="light">â˜€ Light Mode</option>
            <option value="dark">ðŸŒ™ Dark Mode</option>
          </select>
        </div>
      </header>

      {/* Main Content */}
      <main className="main-content">
        <div className="welcome-wrapper">
          <h1>Welcome to Our Sign Language Platform</h1>
          <p>
            Explore interactive lessons, quizzes, and personalized learning experiences.
          </p>
        </div>
        {/* Image Scroller for About Section */}
        <section className="about-section">
          <h2>About Our Website</h2>
          <div className="image-scroller">
            <div className="scroller-container">
              <img src="/assets/image1.jpg" alt="About 1" />
              <img src="/assets/image2.jpg" alt="About 2" />
              <img src="/assets/image3.jpg" alt="About 3" />
              <img src="/assets/image4.jpg" alt="About 4" />
            </div>
          </div>
        </section>
      </main>

      {/* Footer */}
      <footer className="site-footer">
        <p>Â© 2025 Sign Language Platform. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
